import React from 'react';
import { Link } from 'react-router-dom';

const items = [
  { id: 1, image: './assets/bunnycloud.png', title: 'Bunny Cloud', description: 'A cute bunny cloud in the sky.' },

  // Add more items
];

const Gallery = () => {
  return (
    <div className="gallery-grid">
      {items.map((item) => (
        <div className="art-piece" key={item.id}>
          <Link to={`/art/${item.id}`}>
            <img src={item.image} alt={item.title} />
            <h3>{item.title}</h3>
          </Link>
        </div>
      ))}
    </div>
  );
}

export default Gallery;
